package day09_string_methods_for_loop;

public class ForLoopQuestion2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	for (int i=100; i>=0; i=i-2) { //i=i-2  i-=2
		System.out.print(i + " ");
	}
	System.out.println();
	for (int i=2; i<=200; i+=2) {
		System.out.print(i+ ", ");
	}
	}
	

}
